function mostrarMensajeFinal($porcentaje)
{
    if ($porcentaje < 50) {
        return ["Seguí participando", "https://d3hcrfnfnzslzm.cloudfront.net/resources/plates/2023-10-22/-1022202221.jpg"];
    } elseif ($porcentaje < 75) {
        return ["Bien", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRku3Pak-tK8Uw0bcxb-UIDiEnhI-qS0rYwbw&s"];
    } elseif ($porcentaje < 100) {
        return ["Muy bien", "data:image/jpeg;base64,"];
    } else {
        return ["¡Excelente!", "data:image/jpeg;base64,"];
    }
}
