    $puntaje = 0;
    $total_correctas = 0;
    $resultados = [];

    foreach ($respuestas_correctas as $clave => $respuesta_correcta) {
        if ($clave == "pregunta10") {
            $usuario = $_POST[$clave] ?? [];
            $usuario = is_array($usuario) ? $usuario : [];

            sort($usuario);
            $correctas = $respuesta_correcta;
            sort($correctas);

            if ($usuario == $correctas) {
                $puntaje += 10;
                $total_correctas++;
                $resultados[$clave] = '<div class="alert alert-success">✔ ' . ucfirst($clave) . ': Correcta</div>';
            } else {
                $correctas_str = implode(", ", array_map("strtoupper", $correctas));
                $resultados[$clave] = '<div class="alert alert-danger">✘ ' . ucfirst($clave) . ': Incorrecta. Las respuestas correctas son: ' . $correctas_str . '</div>';
            }
        } else {
            $respuesta_usuario = $_POST[$clave] ?? null;
            if ($respuesta_usuario == $respuesta_correcta) {
                $puntaje += 10;
                $total_correctas++;
                $resultados[$clave] = '<div class="alert alert-success">✔ ' . ucfirst($clave) . ': Correcta</div>';
            } else {
                $resultados[$clave] = '<div class="alert alert-danger">✘ ' . ucfirst($clave) . ': Incorrecta. La respuesta correcta es: ' . strtoupper($respuesta_correcta) . '</div>';
            }
        }
    }

    $total_preguntas = count($respuestas_correctas);
    $porcentaje = ($total_correctas / $total_preguntas) * 100;
    [$mensajeFinal, $imagen] = mostrarMensajeFinal($porcentaje);
?>

    <h1>Resultados</h1>
